value.value:
{
    "@type": "MessageCard",
    "@context": "http://schema.org/extensions",
    "themeColor": "0076D7",
    "summary": "Pipeline status alert message​​​​",
    "sections": [
        {
            "activityTitle": "Pipeline alert​​​​",
            "facts": [
                {
                    "name": "Activity name:",
                    "value": "@{pipeline().parameters.ProcessName}"
                },
                {
                    "name": "Activity status:",
                    "value": "@{pipeline().parameters.Status}"
                },
                {
                    "name": "Refresh ID:",
                    "value": "@{pipeline().parameters.RefreshId}"
                },
                {
                    "name": "Process ID:",
                    "value": "@{pipeline().parameters.PipelineID}"
                },
                {
                    "name": "Start Time (EST):",
                    "value": "@{pipeline().parameters.StartTime}"
                },
                {
                    "name": "End Time (EST):",
                    "value": "@{pipeline().parameters.EndTime}"
                },
                {
                    "name": "Notification time (EST):",
                    "value": "@{convertFromUtc(utcnow(),'Eastern Standard Time')}"
                }
            ],
            "markdown": true
        }
    ]
}
